package com.ort.usanote.viewModels.inicio

import android.view.View
import androidx.lifecycle.ViewModel
import androidx.navigation.findNavController
import androidx.viewpager2.widget.CompositePageTransformer
import androidx.viewpager2.widget.MarginPageTransformer
import com.ort.usanote.R
import com.ort.usanote.entities.CategoriaInicio
import com.ort.usanote.entities.SliderItem
import com.ort.usanote.fragments.inicio.InicioFragmentDirections
import kotlin.math.abs

class InicioViewModel : ViewModel() {
    private var categoryList: MutableList<CategoriaInicio> = mutableListOf()
    private var sliderItemList: ArrayList<SliderItem> = arrayListOf()

    fun onCategoryClick(v: View, it:Int){
        val category = categoryList[it].nombre
        val action = InicioFragmentDirections.actionInicioFragmentToProductosFragment().setCategoria(category)
        v.findNavController().navigate(action)
    }

    private fun itemsCategory() {
        categoryList.clear()
        categoryList.add(CategoriaInicio("Periferico","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS7Ak2-27I-akU0k4IRiZ-m6JCv21ByS1-kk1Wo0Q3SQLAHlVs6lEqjTjaoBTAeo49eXtQ&usqp=CAU"))
        categoryList.add(CategoriaInicio("GPU","data:image/png;base64,"))
        categoryList.add(CategoriaInicio("Procesador","https://www.creativefabrica.com/wp-content/uploads/2020/06/15/cpu-black-and-white-line-icon-Graphics-4364378-1-1-580x387.jpg"))
        categoryList.add(CategoriaInicio("RAM","https://www.ato.com.my/image/ato/image/data/Icon/Ram.png"))
    }
    private fun itemSlider() {
        sliderItemList.add(SliderItem(R.drawable.productos,"productos"))
        sliderItemList.add(SliderItem(R.drawable.registro,"regristro"))
        sliderItemList.add(SliderItem(R.drawable.contacto,"contacto"))

    }



    fun NavigateToAction(pos:Int,v:View){

        if(getSliderItem(pos).nombre == "productos"){
            val action = InicioFragmentDirections.actionInicioFragmentToProductosFragment()
            v.findNavController().navigate(action)
        }else if(getSliderItem(pos).nombre == "regristro"){
            val action = InicioFragmentDirections.actionInicioFragmentToRegistroFragment()
            v.findNavController().navigate(action)
        }else{
            val action = InicioFragmentDirections.actionInicioFragmentToContactoFragment()
            v.findNavController().navigate(action)
        }
    }

    fun getCompositePageTransformer():CompositePageTransformer{
        val ret = CompositePageTransformer()
        ret.addTransformer(MarginPageTransformer(40))
        ret.addTransformer { page, position ->
            val r :Float = 1 - abs(position)
            page.scaleY = 0.85f + r * 0.15f
        }
        return ret
    }
    fun getCategoryList():MutableList<CategoriaInicio>{
        itemsCategory()
        return this.categoryList
    }
    fun getSliderItems():ArrayList<SliderItem>{
        itemSlider()
        return this.sliderItemList
    }
    fun getSliderItem(it:Int):SliderItem{
        itemSlider()
        return this.sliderItemList[it]
    }

}